import { useEffect, useRef } from "react"

export default function Plasma({
  color = "#8b5cf6",
  speed = 1,
  direction = "forward",
  scale = 1,
  opacity = 0.3,
  mouseInteractive = false,
}: {
  color?: string
  speed?: number
  direction?: "forward" | "reverse"
  scale?: number
  opacity?: number
  mouseInteractive?: boolean
}) {
  const canvasRef = useRef<HTMLCanvasElement>(null)
  const animationRef = useRef<number>(0)
  const timeRef = useRef<number>(0)
  const mousePosRef = useRef<{ x: number; y: number }>({ x: 0.5, y: 0.5 })

  useEffect(() => {
    const canvas = canvasRef.current
    if (!canvas) return

    const ctx = canvas.getContext("2d")
    if (!ctx) return

    const resizeCanvas = () => {
      canvas.width = window.innerWidth
      canvas.height = window.innerHeight
    }

    resizeCanvas()
    window.addEventListener("resize", resizeCanvas)

    // Mouse tracking
    const handleMouseMove = (e: MouseEvent) => {
      if (!mouseInteractive) return
      mousePosRef.current = {
        x: e.clientX / window.innerWidth,
        y: e.clientY / window.innerHeight,
      }
    }

    window.addEventListener("mousemove", handleMouseMove)

    // Animation loop
    const animate = (timestamp: number) => {
      if (!ctx || !canvas) return

      const deltaTime = timestamp - timeRef.current
      timeRef.current = timestamp

      const width = canvas.width
      const height = canvas.height

      // Clear canvas
      ctx.clearRect(0, 0, width, height)

      // Create plasma effect
      const imageData = ctx.createImageData(width, height)
      const data = imageData.data

      for (let y = 0; y < height; y++) {
        for (let x = 0; x < width; x++) {
          const index = (y * width + x) * 4

          // Plasma algorithm
          let value =
            Math.sin((x / 50) * scale + timeRef.current * 0.001 * speed * (direction === "forward" ? 1 : -1)) +
            Math.sin((y / 30) * scale + timeRef.current * 0.002 * speed * (direction === "forward" ? 1 : -1)) +
            Math.sin(
              ((x + y) / 60) * scale +
                timeRef.current * 0.0015 * speed * (direction === "forward" ? 1 : -1)
            ) +
            Math.sin(
              Math.sqrt(x * x + y * y) / 8.0 * scale +
                timeRef.current * 0.001 * speed * (direction === "forward" ? 1 : -1)
            )

          // Normalize to 0-1 range
          value = (value + 4) / 8

          // Apply mouse interaction
          if (mouseInteractive) {
            const dx = x - mousePosRef.current.x * width
            const dy = y - mousePosRef.current.y * height
            const distance = Math.sqrt(dx * dx + dy * dy)
            const mouseInfluence = Math.max(0, 1 - distance / 300)
            value += mouseInfluence * 0.5
          }

          // Convert to RGB
          const r = parseInt(color.slice(1, 3), 16) * value * opacity
          const g = parseInt(color.slice(3, 5), 16) * value * opacity
          const b = parseInt(color.slice(5, 7), 16) * value * opacity

          data[index] = r // R
          data[index + 1] = g // G
          data[index + 2] = b // B
          data[index + 3] = 255 * opacity // A
        }
      }

      ctx.putImageData(imageData, 0, 0)

      animationRef.current = requestAnimationFrame(animate)
    }

    animationRef.current = requestAnimationFrame(animate)

    return () => {
      window.removeEventListener("resize", resizeCanvas)
      window.removeEventListener("mousemove", handleMouseMove)
      cancelAnimationFrame(animationRef.current)
    }
  }, [color, speed, direction, scale, opacity, mouseInteractive])

  return <canvas ref={canvasRef} className="absolute inset-0 h-full w-full" />
}","file_path":"/Users/ORDEROFCODE/COM-PSU-Rizal/components/plasma.tsx"}

{"content":""use client"

import { useState, useEffect, useRef } from "react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import { 
  Mic, 
  MicOff, 
  Video, 
  VideoOff, 
  PhoneOff, 
  ScreenShare, 
  ScreenShareOff,
  MessageCircle,
  Users,
  MoreHorizontal
} from "lucide-react"
import { useRouter } from "next/navigation"

export default function MeetingRoom() {
  const router = useRouter()
  const [isMuted, setIsMuted] = useState(false)
  const [isVideoOff, setIsVideoOff] = useState(false)
  const [isScreenSharing, setIsScreenSharing] = useState(false)
  const [showChat, setShowChat] = useState(false)
  const [chatMessages, setChatMessages] = useState<Array<{id: number, user: string, message: string, time: string}>>([])
  const [newMessage, setNewMessage] = useState("")
  const [participants, setParticipants] = useState(1)

  const localVideoRef = useRef<HTMLVideoElement>(null)
  const remoteVideoRef = useRef<HTMLVideoElement>(null)

  // Simulate participants joining
  useEffect(() => {
    const interval = setInterval(() => {
      if (participants < 12) {
        setParticipants(prev => prev + 1)
      }
    }, 10000)

    return () => clearInterval(interval)
  }, [participants])

  // Simulate chat messages
  useEffect(() => {
    const messages = [
      {id: 1, user: "Alex Johnson", message: "Hi everyone! Ready to start?", time: "10:00 AM"},
      {id: 2, user: "Maria Garcia", message: "Yes, I've shared the documents in the chat", time: "10:01 AM"},
      {id: 3, user: "David Chen", message: "Thanks Maria, I can see them", time: "10:02 AM"},
    ]

    setChatMessages(messages)
  }, [])

  const toggleMute = () => setIsMuted(!isMuted)
  const toggleVideo = () => setIsVideoOff(!isVideoOff)
  const toggleScreenShare = () => setIsScreenSharing(!isScreenSharing)
  const toggleChat = () => setShowChat(!showChat)

  const sendMessage = () => {
    if (newMessage.trim() === "") return

    const newMsg = {
      id: chatMessages.length + 1,
      user: "You",
      message: newMessage,
      time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    }

    setChatMessages(prev => [...prev, newMsg])
    setNewMessage("")
  }

  const leaveMeeting = () => {
    router.push("/")
  }

  return (
    <div className="flex h-screen bg-black text-white">
      {/* Main Video Area */}
      <div className="flex-1 flex flex-col">
        {/* Top Bar */}
        <div className="p-4 flex justify-between items-center bg-black/50 backdrop-blur-sm">
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 rounded-full bg-red-500 animate-pulse"></div>
            <span className="text-sm font-medium">Meeting in progress</span>
          </div>
          <div className="flex items-center gap-2 text-sm">
            <Users className="w-4 h-4" />
            <span>{participants} participants</span>
          </div>
        </div>

        {/* Video Grid */}
        <div className="flex-1 grid grid-cols-2 gap-4 p-4">
          {/* Local Video */}
          <div className="relative rounded-lg overflow-hidden bg-gray-900">
            <div className="absolute inset-0 bg-gradient-to-br from-purple-900/20 to-blue-900/20"></div>
            <div className="absolute bottom-4 left-4 bg-black/50 rounded-full px-3 py-1 text-sm">
              You
            </div>
            {isVideoOff ? (
              <div className="absolute inset-0 flex items-center justify-center">
                <div className="bg-gray-700 rounded-full w-24 h-24 flex items-center justify-center">
                  <VideoOff className="w-12 h-12 text-gray-400" />
                </div>
              </div>
            ) : (
              <div className="absolute inset-0 flex items-center justify-center">
                <div className="bg-gray-700 rounded-full w-24 h-24 flex items-center justify-center">
                  <Video className="w-12 h-12 text-gray-400" />
                </div>
              </div>
            )}
          </div>

          {/* Remote Participant 1 */}
          <div className="relative rounded-lg overflow-hidden bg-gray-900">
            <div className="absolute inset-0 bg-gradient-to-br from-blue-900/20 to-teal-900/20"></div>
            <div className="absolute bottom-4 left-4 bg-black/50 rounded-full px-3 py-1 text-sm">
              Alex Johnson
            </div>
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="bg-gray-700 rounded-full w-24 h-24 flex items-center justify-center">
                <Video className="w-12 h-12 text-gray-400" />
              </div>
            </div>
          </div>

          {/* Remote Participant 2 */}
          <div className="relative rounded-lg overflow-hidden bg-gray-900">
            <div className="absolute inset-0 bg-gradient-to-br from-green-900/20 to-emerald-900/20"></div>
            <div className="absolute bottom-4 left-4 bg-black/50 rounded-full px-3 py-1 text-sm">
              Maria Garcia
            </div>
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="bg-gray-700 rounded-full w-24 h-24 flex items-center justify-center">
                <Video className="w-12 h-12 text-gray-400" />
              </div>
            </div>
          </div>

          {/* Screen Share */}
          <div className="relative rounded-lg overflow-hidden bg-gray-900 col-span-2">
            <div className="absolute inset-0 bg-gradient-to-br from-indigo-900/20 to-purple-900/20"></div>
            <div className="absolute top-4 left-4 bg-black/50 rounded-full px-3 py-1 text-sm flex items-center gap-2">
              <ScreenShare className="w-4 h-4" />
              Maria is sharing screen
            </div>
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="text-center">
                <div className="bg-gray-700 rounded-lg w-16 h-16 flex items-center justify-center mx-auto mb-4">
                  <Monitor className="w-8 h-8 text-gray-400" />
                </div>
                <p className="text-gray-400">Presentation slides</p>
              </div>
            </div>
          </div>
        </div>

        {/* Controls */}
        <div className="p-6 bg-black/50 backdrop-blur-sm">
          <div className="flex justify-center items-center gap-6">
            <Button 
              size="icon" 
              variant={isMuted ? "destructive" : "secondary"}
              onClick={toggleMute}
              className="rounded-full w-14 h-14"
            >
              {isMuted ? <MicOff className="w-6 h-6" /> : <Mic className="w-6 h-6" />}
            </Button>

            <Button 
              size="icon" 
              variant={isVideoOff ? "destructive" : "secondary"}
              onClick={toggleVideo}
              className="rounded-full w-14 h-14"
            >
              {isVideoOff ? <VideoOff className="w-6 h-6" /> : <Video className="w-6 h-6" />}
            </Button>

            <Button 
              size="icon" 
              variant={isScreenSharing ? "default" : "secondary"}
              onClick={toggleScreenShare}
              className="rounded-full w-14 h-14"
            >
              {isScreenSharing ? <ScreenShareOff className="w-6 h-6" /> : <ScreenShare className="w-6 h-6" />}
            </Button>

            <Button 
              size="icon" 
              variant="secondary"
              onClick={toggleChat}
              className="rounded-full w-14 h-14 relative"
            >
              <MessageCircle className="w-6 h-6" />
              {chatMessages.length > 0 && (
                <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                  {chatMessages.length}
                </span>
              )}
            </Button>

            <Button 
              size="icon" 
              variant="destructive"
              onClick={leaveMeeting}
              className="rounded-full w-14 h-14"
            >
              <PhoneOff className="w-6 h-6" />
            </Button>
          </div>
        </div>
      </div>

      {/* Chat Panel */}
      {showChat && (
        <div className="w-80 bg-gray-900/50 backdrop-blur-sm border-l border-gray-700 flex flex-col">
          <div className="p-4 border-b border-gray-700 flex items-center justify-between">
            <h3 className="font-medium">Chat</h3>
            <Button 
              size="icon" 
              variant="ghost" 
              onClick={toggleChat}
              className="h-8 w-8"
            >
              <X className="w-4 h-4" />
            </Button>
          </div>

          <div className="flex-1 overflow-y-auto p-4 space-y-4">
            {chatMessages.map((msg) => (
              <div key={msg.id} className="text-sm">
                <div className="flex justify-between items-center mb-1">
                  <span className="font-medium">{msg.user}</span>
                  <span className="text-xs text-gray-400">{msg.time}</span>
                </div>
                <p className="text-gray-200">{msg.message}</p>
              </div>
            ))}
          </div>

          <div className="p-4 border-t border-gray-700">
            <div className="flex gap-2">
              <Input 
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
                placeholder="Type a message..."
                className="flex-1"
              />
              <Button size="icon" onClick={sendMessage}>
                <Send className="w-4 h-4" />
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

// Icons that might be missing from imports
function Monitor() {
  return (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <rect width="20" height="12" x="2" y="3" rx="2" />
