import type React from "react"
import { useState } from "react"
import { useToast } from "@/hooks/use-toast"
import { AuthCard } from "@/components/auth/auth-card"
import { useAuth } from "@/contexts/AuthContext"
import { useRouter } from "next/navigation"

export default function AuthPage() {
  const [isLoading, setIsLoading] = useState(false)
  const [password, setPassword] = useState("")
  const [email, setEmail] = useState("")
  const [rememberMe, setRememberMe] = useState(false)
  const [firstName, setFirstName] = useState("")
  const [lastName, setLastName] = useState("")
  const [role, setRole] = useState<"student" | "faculty">("student")
  const { toast } = useToast()
  const { signIn, signUp, forgotPassword } = useAuth()
  const router = useRouter()

  const validateEmail = (email: string) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)
  }

  const handleSignIn = async (e: React.FormEvent) => {
    e.preventDefault()

    if (!validateEmail(email)) {
      toast({
        title: "Invalid email",
        description: "Please enter a valid email address.",
        variant: "destructive",
      })
      return
    }

    setIsLoading(true)

    try {
      const result = await signIn({ email, password })

      if (result.error) {
        toast({
          title: "Sign in failed",
          description: result.error.message || "Invalid credentials. Please try again.",
          variant: "destructive",
        })
      } else {
        toast({
          title: "Signed in successfully!",
          description: "Welcome back to your account.",
        })
        // Redirect to dashboard
        router.push("/dashboard")
      }
    } catch (error) {
      toast({
        title: "Sign in failed",
        description: "An unexpected error occurred. Please try again.",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  const handleSignUp = async (e: React.FormEvent) => {
    e.preventDefault()

    if (!validateEmail(email)) {
      toast({
        title: "Invalid email",
        description: "Please enter a valid email address.",
        variant: "destructive",
      })
      return
    }

    if (password.length < 6) {
      toast({
        title: "Password too short",
        description: "Password must be at least 6 characters long.",
        variant: "destructive",
      })
      return
    }

    if (!firstName || !lastName) {
      toast({
        title: "Missing information",
        description: "Please enter your first and last name.",
        variant: "destructive",
      })
      return
    }

    setIsLoading(true)

    try {
      const result = await signUp({ 
        email, 
        password, 
        firstName, 
        lastName, 
        role 
      })

      if (result.error) {
        toast({
          title: "Sign up failed",
          description: result.error.message || "Failed to create account. Please try again.",
          variant: "destructive",
        })
      } else {
        toast({
          title: "Account created!",
          description: "Your account has been created successfully. Please check your email for verification.",
        })
        // Redirect to dashboard
        router.push("/dashboard")
      }
    } catch (error) {
      toast({
        title: "Sign up failed",
        description: "An unexpected error occurred. Please try again.",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  const handleSocialLogin = (provider: string) => {
    toast({
      title: `${provider} login`,
      description: `Redirecting to ${provider}...`,
    })
  }

  const handleForgotPassword = async () => {
    if (!validateEmail(email)) {
      toast({
        title: "Invalid email",
        description: "Please enter a valid email address.",
        variant: "destructive",
      })
      return
    }

    try {
      const result = await forgotPassword(email)

      if (result.error) {
        toast({
          title: "Failed to send reset link",
          description: result.error.message || "Failed to send password reset link. Please try again.",
          variant: "destructive",
        })
      } else {
        toast({
          title: "Reset link sent",
          description: "Check your email for password reset instructions.",
        })
      }
    } catch (error) {
      toast({
        title: "Failed to send reset link",
        description: "An unexpected error occurred. Please try again.",
        variant: "destructive",
      })
    }
  }

  return (
    <div
      className="min-h-screen flex items-center justify-center p-4"
      style={{
        backgroundImage: "url('/bg.jpeg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
      }}
    >
      <AuthCard
        isLoading={isLoading}
        email={email}
        setEmail={setEmail}
        password={password}
        setPassword={setPassword}
        rememberMe={rememberMe}
        setRememberMe={setRememberMe}
        firstName={firstName}
        setFirstName={setFirstName}
        lastName={lastName}
        setLastName={setLastName}
        role={role}
        setRole={setRole}
        onSignIn={handleSignIn}
        onSignUp={handleSignUp}
        onSocialLogin={handleSocialLogin}
        onForgotPassword={handleForgotPassword}
      />
    </div>
  )
}"}

{"content":"-- Create users table
create table if not exists users (
  id uuid references auth.users on delete cascade not null primary key,
  email text unique not null,
  first_name text not null,
  last_name text not null,
  role text check (role in ('student', 'faculty', 'admin')) not null default 'student',
  phone text,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null,
  updated_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Create classes table
create table if not exists classes (
  id uuid default gen_random_uuid() primary key,
  name text not null,
  description text,
  faculty_id uuid references users(id) on delete cascade not null,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null,
  updated_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Create enrollments table (many-to-many relationship between students and classes)
create table if not exists enrollments (
  id uuid default gen_random_uuid() primary key,
  student_id uuid references users(id) on delete cascade not null,
  class_id uuid references classes(id) on delete cascade not null,
  enrolled_at timestamp with time zone default timezone('utc'::text, now()) not null,
  unique(student_id, class_id)
);

-- Create attendance table
create table if not exists attendance (
  id uuid default gen_random_uuid() primary key,
  student_id uuid references users(id) on delete cascade not null,
  class_id uuid references classes(id) on delete cascade not null,
  meeting_id uuid not null, -- References meeting ID from your video conferencing system
  status text check (status in ('present', 'absent', 'late')) not null,
  recorded_at timestamp with time zone default timezone('utc'::text, now()) not null,
  recorded_by uuid references users(id) on delete cascade not null
);

-- Create assignments table
create table if not exists assignments (
  id uuid default gen_random_uuid() primary key,
  class_id uuid references classes(id) on delete cascade not null,
  title text not null,
  description text,
  due_date timestamp with time zone,
  max_points integer,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null,
  updated_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Create submissions table
create table if not exists submissions (
  id uuid default gen_random_uuid() primary key,
  assignment_id uuid references assignments(id) on delete cascade not null,
  student_id uuid references users(id) on delete cascade not null,
  file_path text, -- Path to the file in Supabase Storage
  grade integer,
  feedback text,
  submitted_at timestamp with time zone default timezone('utc'::text, now()) not null,
  graded_at timestamp with time zone,
  graded_by uuid references users(id) on delete cascade,
  unique(assignment_id, student_id)
);

-- Create grades table (alternative to storing grades in submissions)
create table if not exists grades (
  id uuid default gen_random_uuid() primary key,
  student_id uuid references users(id) on delete cascade not null,
  class_id uuid references classes(id) on delete cascade not null,
  assignment_id uuid references assignments(id) on delete cascade,
  grade_type text check (grade_type in ('assignment', 'exam', 'participation', 'final')) not null,
  points_earned numeric(5,2),
  points_possible numeric(5,2),
  recorded_at timestamp with time zone default timezone('utc'::text, now()) not null,
  recorded_by uuid references users(id) on delete cascade not null
);

-- Set up Row Level Security (RLS)
alter table users enable row level security;
alter table classes enable row level security;
alter table enrollments enable row level security;
alter table attendance enable row level security;
alter table assignments enable row level security;
alter table submissions enable row level security;
alter table grades enable row level security;

-- RLS Policies
-- Users can only see their own record
create policy "Users can view their own record" on users
  for select using (auth.uid() = id);

create policy "Users can update their own record" on users
  for update using (auth.uid() = id);

-- Faculty can view classes they created, students can view classes they're enrolled in
create policy "Faculty can view their classes" on classes
  for select using (auth.uid() = faculty_id);

create policy "Students can view enrolled classes" on classes
  for select using (
    id in (
      select class_id from enrollments where student_id = auth.uid()
    )
  );

-- Faculty can create classes
create policy "Faculty can create classes" on classes
  for insert with check (
    auth.uid() in (
      select id from users where id = auth.uid() and role = 'faculty'
    )
  );

-- Faculty can update their own classes
create policy "Faculty can update their classes" on classes
  for update using (auth.uid() = faculty_id);

-- Students and faculty can view enrollments for their classes
create policy "Users can view enrollments for their classes" on enrollments
  for select using (
    class_id in (
      select id from classes where faculty_id = auth.uid()
    ) or student_id = auth.uid()
  );

-- Students can enroll themselves, faculty can enroll students in their classes
create policy "Users can create enrollments" on enrollments
  for insert with check (
    student_id = auth.uid() or 
    class_id in (
      select id from classes where faculty_id = auth.uid()
    )
  );

-- Faculty can view attendance for their classes, students can view their own attendance
create policy "Users can view attendance" on attendance
  for select using (
    class_id in (
      select id from classes where faculty_id = auth.uid()
    ) or student_id = auth.uid()
  );

-- Faculty can record attendance for their classes
create policy "Faculty can record attendance" on attendance
  for insert with check (
    class_id in (
      select id from classes where faculty_id = auth.uid()
    ) and recorded_by = auth.uid()
  );

-- Faculty can view assignments for their classes, students can view assignments for enrolled classes
create policy "Users can view assignments" on assignments
  for select using (
    class_id in (
      select id from classes where faculty_id = auth.uid()
    ) or class_id in (
      select class_id from enrollments where student_id = auth.uid()
    )
  );

-- Faculty can create assignments for their classes
create policy "Faculty can create assignments" on assignments
  for insert with check (
    class_id in (
      select id from classes where faculty_id = auth.uid()
    )
  );

-- Students can view their own submissions, faculty can view submissions for their classes
create policy "Users can view submissions" on submissions
  for select using (
    student_id = auth.uid() or 
    assignment_id in (
      select id from assignments where class_id in (
        select id from classes where faculty_id = auth.uid()
      )
    )
  );

-- Students can create submissions, faculty can update grades
create policy "Students can create submissions" on submissions
  for insert with check (student_id = auth.uid());

create policy "Faculty can grade submissions" on submissions
  for update using (
    assignment_id in (
      select id from assignments where class_id in (
        select id from classes where faculty_id = auth.uid()
      )
    )
  );

-- Users can view their own grades or faculty can view grades for their classes
create policy "Users can view grades" on grades
  for select using (
    student_id = auth.uid() or
    class_id in (
      select id from classes where faculty_id = auth.uid()
    )
  );

-- Faculty can record grades for their classes
create policy "Faculty can record grades" on grades
  for insert with check (
    class_id in (
      select id from classes where faculty_id = auth.uid()
    ) and recorded_by = auth.uid()
  );

-- Faculty can update grades they recorded
create policy "Faculty can update grades" on grades
  for update using (
    recorded_by = auth.uid()
  );

-- Create storage bucket for assignments
insert into storage.buckets (id, name, public)
  values ('assignments', 'assignments', false)
  on conflict (id) do nothing;

-- Storage policies for assignments bucket
create policy "Users can upload assignments" on storage.objects
  for insert with check (
    bucket_id = 'assignments' and
    (auth.role() = 'authenticated')
  );

create policy "Users can view their own assignments" on storage.objects
  for select using (
    bucket_id = 'assignments' and
    (
      owner = auth.uid() or
      name ilike auth.uid() || '/%' or
      auth.uid() in (
        select faculty_id from classes c
        join assignments a on a.class_id = c.id
        where a.id = (
          select assignment_id from submissions
          where file_path = storage.objects.name
        )
      )
    )
  );

create policy "Users can delete their own assignments" on storage.objects
  for delete using (
    bucket_id = 'assignments' and
    (owner = auth.uid() or name ilike auth.uid() || '/%')
  );","file_path":"/Users/ORDEROFCODE/COM-PSU-Rizal/supabase/migrations/001_initial_schema.sql"}

{"content":"import { supabase } from '@/lib/supabaseClient'

// User operations
export const getUserProfile = async (userId: string) => {
  const { data, error } = await supabase
    .from('users')
    .select('*')
    .eq('id', userId)
    .single()

  return { data, error }
}

export const updateUserProfile = async (userId: string, updates: Partial<any>) => {
  const { data, error } = await supabase
    .from('users')
    .update(updates)
    .eq('id', userId)
    .select()
    .single()

  return { data, error }
}

// Class operations
export const getClasses = async () => {
  const { data, error } = await supabase
    .from('classes')
    .select(`
      *,
      faculty:users(first_name, last_name)
    `)

  return { data, error }
}

export const getClassById = async (classId: string) => {
  const { data, error } = await supabase
    .from('classes')
    .select(`
      *,
      faculty:users(first_name, last_name),
      enrollments(*),
      assignments(*)
    `)
    .eq('id', classId)
    .single()

  return { data, error }
}

export const createClass = async (classData: { name: string; description?: string; faculty_id: string }) => {
  const { data, error } = await supabase
    .from('classes')
    .insert([classData])
    .select()
    .single()

  return { data, error }
}

export const updateClass = async (classId: string, updates: Partial<any>) => {
  const { data, error } = await supabase
    .from('classes')
    .update(updates)
    .eq('id', classId)
    .select()
    .single()

  return { data, error }
}

export const deleteClass = async (classId: string) => {
  const { error } = await supabase
    .from('classes')
    .delete()
    .eq('id', classId)

  return { error }
}

// Enrollment operations
export const getEnrollments = async (userId: string) => {
  const { data, error } = await supabase
    .from('enrollments')
    .select(`
      *,
      class:classes(name, description),
      student:users(first_name, last_name)
    `)
    .or(`student_id.eq.${userId},class.faculty_id.eq.${userId}`)

  return { data, error }
}

export const enrollInClass = async (studentId: string, classId: string) => {
  const { data, error } = await supabase
    .from('enrollments')
    .insert([{ student_id: studentId, class_id: classId }])
    .select()
    .single()

  return { data, error }
}

export const unenrollFromClass = async (enrollmentId: string) => {
  const { error } = await supabase
    .from('enrollments')
    .delete()
    .eq('id', enrollmentId)

  return { error }
}

// Attendance operations
export const getAttendance = async (classId: string, studentId?: string) => {
  let query = supabase
    .from('attendance')
    .select(`
      *,
      student:users(first_name, last_name),
      recorder:users(first_name, last_name)
    `)
    .eq('class_id', classId)

  if (studentId) {
    query = query.eq('student_id', studentId)
  }

  const { data, error } = await query

  return { data, error }
}

export const recordAttendance = async (attendanceData: {
  student_id: string
  class_id: string
  meeting_id: string
  status: 'present' | 'absent' | 'late'
  recorded_by: string
}) => {
  const { data, error } = await supabase
    .from('attendance')
    .insert([attendanceData])
    .select()
    .single()

  return { data, error }
}

// Assignment operations
export const getAssignments = async (classId: string) => {
  const { data, error } = await supabase
    .from('assignments')
    .select('*')
    .eq('class_id', classId)
    .order('due_date', { ascending: true })

  return { data, error }
}

export const getAssignmentById = async (assignmentId: string) => {
  const { data, error } = await supabase
    .from('assignments')
    .select('*')
    .eq('id', assignmentId)
    .single()

  return { data, error }
}

export const createAssignment = async (assignmentData: {
  class_id: string
  title: string
  description?: string
  due_date?: string
  max_points?: number
}) => {
  const { data, error } = await supabase
    .from('assignments')
    .insert([assignmentData])
    .select()
    .single()

  return { data, error }
}

export const updateAssignment = async (assignmentId: string, updates: Partial<any>) => {
  const { data, error } = await supabase
    .from('assignments')
    .update(updates)
    .eq('id', assignmentId)
    .select()
    .single()

  return { data, error }
}

export const deleteAssignment = async (assignmentId: string) => {
  const { error } = await supabase
    .from('assignments')
    .delete()
    .eq('id', assignmentId)

  return { error }
}

// Submission operations
export const getSubmissions = async (assignmentId: string, studentId?: string) => {
  let query = supabase
    .from('submissions')
    .select(`
      *,
      student:users(first_name, last_name)
    `)
    .eq('assignment_id', assignmentId)

  if (studentId) {
    query = query.eq('student_id', studentId)
  }

  const { data, error } = await query

  return { data, error }
}

export const submitAssignment = async (submissionData: {
  assignment_id: string
  student_id: string
  file_path?: string
}) => {
  const { data, error } = await supabase
    .from('submissions')
    .insert([submissionData])
    .select()
    .single()

  return { data, error }
}

export const gradeSubmission = async (submissionId: string, grade: number, feedback?: string) => {
  const { data, error } = await supabase
    .from('submissions')
    .update({
      grade,
      feedback,
      graded_at: new Date().toISOString(),
    })
    .eq('id', submissionId)
    .select()
    .single()

  return { data, error }
}

// Grade operations
export const getGrades = async (studentId: string, classId?: string) => {
  let query = supabase
    .from('grades')
    .select(`
      *,
      recorder:users(first_name, last_name)
    `)
    .eq('student_id', studentId)

  if (classId) {
    query = query.eq('class_id', classId)
  }

  const { data, error } = await query

  return { data, error }
}

export const recordGrade = async (gradeData: {
  student_id: string
  class_id: string
  assignment_id?: string
  grade_type: string
  points_earned: number
  points_possible: number
  recorded_by: string
}) => {
  const { data, error } = await supabase
    .from('grades')
    .insert([gradeData])
    .select()
    .single()

  return { data, error }
}

// Storage operations
export const uploadFile = async (filePath: string, file: File) => {
  const { data, error } = await supabase.storage
    .from('assignments')
    .upload(filePath, file, {
      cacheControl: '3600',
      upsert: false
    })

  return { data, error }
}

export const downloadFile = async (filePath: string) => {
  const { data, error } = await supabase.storage
    .from('assignments')
    .download(filePath)

  return { data, error }
}

export const deleteFile = async (filePath: string) => {
  const { error } = await supabase.storage
    .from('assignments')
    .remove([filePath])

  return { error }
}","file_path":"/Users/ORDEROFCODE/COM-PSU-Rizal/lib/supabaseService.ts"}

