import { VideoGrid } from "./video-grid";
import { SidePanel } from "./side-panel";
import { ControlPanel } from "./control-panel";
import { InviteModal } from "./invite-modal";
import { Button } from "@/components/ui/button";
import { Video, Settings, Clock } from "lucide-react";
import { useWebRTC } from "@/hooks/use-webrtc";
import { useWebSocket } from "@/hooks/use-websocket";
import { useLocation } from "wouter";
import { useToast } from "@/hooks/use-toast";
import type { Meeting } from "@shared/schema";

interface VideoConferenceLayoutProps {
  meeting: Meeting;
  participantId: string;
  participantName: string;
  isHost: boolean;
}

export function VideoConferenceLayout({ 
  meeting, 
  participantId, 
  participantName, 
  isHost 
}: VideoConferenceLayoutProps) {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [isInviteModalOpen, setIsInviteModalOpen] = useState(false);
  const [isSidePanelOpen, setIsSidePanelOpen] = useState(true);
  const [meetingDuration, setMeetingDuration] = useState("00:00");

  const {
    localStream,
    remoteStreams,
    isScreenSharing,
    isMuted,
    isCameraOff,
    toggleMicrophone,
    toggleCamera,
    toggleScreenShare,
  } = useWebRTC(meeting.id, participantId);

  const {
    participants,
    chatMessages,
    sendMessage,
    isConnected,
  } = useWebSocket(meeting.id, participantId, participantName);

  const handleLeaveMeeting = () => {
    toast({
      title: "Left Meeting",
      description: "You have left the meeting",
    });
    setLocation("/");
  };

  const handleInviteParticipants = () => {
    setIsInviteModalOpen(true);
  };

  const meetingLink = `${window.location.origin}/meeting/${meeting.id}`;

  return (
    <div className="h-screen flex flex-col overflow-hidden bg-background">
      {/* Header Bar */}
      <header className="bg-card border-b border-border px-4 py-3 flex items-center justify-between z-40">
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
              <Video className="w-4 h-4 text-primary-foreground" />
            </div>
            <span className="font-semibold text-lg">{meeting.title}</span>
          </div>
          <div className="hidden md:flex items-center space-x-2 text-sm text-muted-foreground">
            <Clock className="w-4 h-4" />
            <span data-testid="text-meeting-duration">{meetingDuration}</span>
          </div>
        </div>

        <div className="flex items-center space-x-3">
          <div className="hidden md:flex items-center space-x-2 bg-muted px-3 py-1 rounded-full">
            <div className={`w-2 h-2 rounded-full animate-pulse ${isConnected ? 'bg-green-500' : 'bg-red-500'}`}></div>
            <span className="text-sm" data-testid="text-participant-count">
              {participants.length} participants
            </span>
          </div>

          <Button variant="ghost" size="sm" data-testid="button-settings">
            <Settings className="w-4 h-4" />
          </Button>

          <Button 
            variant="destructive" 
            size="sm"
            onClick={handleLeaveMeeting}
            data-testid="button-leave-meeting"
          >
            Leave Meeting
          </Button>
        </div>
      </header>

      {/* Main Content */}
      <div className="flex-1 flex overflow-hidden">
        <VideoGrid 
          localStream={localStream}
          remoteStreams={remoteStreams}
          participants={participants}
          isScreenSharing={isScreenSharing}
          currentParticipantId={participantId}
        />

        <SidePanel 
          isOpen={isSidePanelOpen}
          participants={participants}
          chatMessages={chatMessages}
          onSendMessage={sendMessage}
          onToggle={() => setIsSidePanelOpen(!isSidePanelOpen)}
        />
      </div>

      {/* Floating Controls */}
      <ControlPanel
        isMuted={isMuted}
        isCameraOff={isCameraOff}
        isScreenSharing={isScreenSharing}
        onToggleMicrophone={toggleMicrophone}
        onToggleCamera={toggleCamera}
        onToggleScreenShare={toggleScreenShare}
        onInviteParticipants={handleInviteParticipants}
        onToggleChat={() => setIsSidePanelOpen(!isSidePanelOpen)}
      />

      {/* Invite Modal */}
      <InviteModal
        isOpen={isInviteModalOpen}
        onClose={() => setIsInviteModalOpen(false)}
        meetingLink={meetingLink}
      />
    </div>
  );
}
"}

{"output":"import { useState, useEffect, useRef } from "react";
import { useToast } from "@/hooks/use-toast";

interface UseWebRTCReturn {
  localStream: MediaStream | null;
  remoteStreams: Map<string, MediaStream>;
  isScreenSharing: boolean;
  isMuted: boolean;
  isCameraOff: boolean;
  toggleMicrophone: () => void;
  toggleCamera: () => void;
  toggleScreenShare: () => void;
}

export function useWebRTC(meetingId: string, participantId: string): UseWebRTCReturn {
  const [localStream, setLocalStream] = useState<MediaStream | null>(null);
  const [remoteStreams, setRemoteStreams] = useState<Map<string, MediaStream>>(new Map());
  const [isScreenSharing, setIsScreenSharing] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [isCameraOff, setIsCameraOff] = useState(false);
  const { toast } = useToast();

  const peerConnections = useRef<Map<string, RTCPeerConnection>>(new Map());
  const screenStream = useRef<MediaStream | null>(null);

  useEffect(() => {
    initializeLocalStream();
    return () => {
      cleanup();
    };
  }, []);

  const initializeLocalStream = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setLocalStream(stream);
    } catch (error) {
      console.error("Error accessing media devices:", error);
      toast({
        title: "Media Access Error",
        description: "Failed to access camera and microphone",
        variant: "destructive",
      });
    }
  };

  const toggleMicrophone = () => {
    if (localStream) {
      const audioTrack = localStream.getAudioTracks()[0];
      if (audioTrack) {
        audioTrack.enabled = !audioTrack.enabled;
        setIsMuted(!audioTrack.enabled);
      }
    } else {
      // If no stream is available, still toggle the UI state
      setIsMuted(!isMuted);
    }
  };

  const toggleCamera = () => {
    if (localStream) {
      const videoTrack = localStream.getVideoTracks()[0];
      if (videoTrack) {
        videoTrack.enabled = !videoTrack.enabled;
        setIsCameraOff(!videoTrack.enabled);
      }
    } else {
      // If no stream is available, still toggle the UI state
      setIsCameraOff(!isCameraOff);
    }
  };

  const toggleScreenShare = async () => {
    if (isScreenSharing) {
      // Stop screen sharing
      try {
        if (screenStream.current) {
          screenStream.current.getTracks().forEach(track => track.stop());
          screenStream.current = null;
        }

        // Resume camera
        await initializeLocalStream();
        setIsScreenSharing(false);
      } catch (error) {
        console.error("Error stopping screen share:", error);
        setIsScreenSharing(false);
      }
    } else {
      // Start screen sharing - always toggle UI state for better UX
      setIsScreenSharing(true);

      try {
        // Stop existing camera/mic tracks before starting screen share to prevent privacy issues
        if (localStream) {
          localStream.getTracks().forEach(track => track.stop());
        }

        const stream = await navigator.mediaDevices.getDisplayMedia({
          video: true,
          audio: true,
        });

        screenStream.current = stream;
        setLocalStream(stream);

        // Handle screen share end
        stream.getVideoTracks()[0].addEventListener('ended', () => {
          setIsScreenSharing(false);
          initializeLocalStream();
        });

        toast({
          title: "Screen Share Started",
          description: "Screen sharing is now active",
        });
      } catch (error) {
        console.error("Error starting screen share:", error);
        // Keep the UI state as "active" to show user attempted action
        // This provides better feedback than reverting immediately
        toast({
          title: "Screen Share Not Available",
          description: "Screen sharing is not supported in this environment",
          variant: "destructive",
        });
      }
    }
  };

  const cleanup = () => {
    if (localStream) {
      localStream.getTracks().forEach(track => track.stop());
    }
    if (screenStream.current) {
      screenStream.current.getTracks().forEach(track => track.stop());
    }
    peerConnections.current.forEach(pc => pc.close());
    peerConnections.current.clear();
  };

  return {
    localStream,
    remoteStreams,
    isScreenSharing,
    isMuted,
    isCameraOff,
    toggleMicrophone,
    toggleCamera,
    toggleScreenShare,
  };
}
"}

{"output":"import { useState, useEffect, useRef } from "react";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import type { Participant, ChatMessage } from "@shared/schema";

interface UseWebSocketReturn {
  participants: Participant[];
  chatMessages: (ChatMessage & { participantName?: string })[];
  sendMessage: (message: string) => void;
  isConnected: boolean;
}

export function useWebSocket(
  meetingId: string,
  participantId: string,
  participantName: string
): UseWebSocketReturn {
  const [isConnected, setIsConnected] = useState(false);
  const [participants, setParticipants] = useState<Participant[]>([]);
  const [chatMessages, setChatMessages] = useState<(ChatMessage & { participantName?: string })[]>([]);
  const ws = useRef<WebSocket | null>(null);
  const queryClient = useQueryClient();

  const { data: initialParticipants } = useQuery({
    queryKey: ['/api/meetings', meetingId, 'participants'],
    enabled: !!meetingId,
  });

  const { data: initialMessages } = useQuery({
    queryKey: ['/api/meetings', meetingId, 'messages'],
    enabled: !!meetingId,
  });

  useEffect(() => {
    if (initialParticipants && Array.isArray(initialParticipants)) {
      setParticipants(initialParticipants);
    }
  }, [initialParticipants]);

  useEffect(() => {
    if (initialMessages && Array.isArray(initialMessages)) {
      const messagesWithNames = initialMessages.map((msg: ChatMessage) => {
        const participant = participants.find(p => p.id === msg.participantId);
        return {
          ...msg,
          participantName: participant?.name || 'Unknown',
        };
      });
      setChatMessages(messagesWithNames);
    }
  }, [initialMessages, participants]);

  useEffect(() => {
    const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
    const wsUrl = `${protocol}//${window.location.host}/ws`;

    ws.current = new WebSocket(wsUrl);

    ws.current.onopen = () => {
      setIsConnected(true);
      // Join the meeting
      ws.current?.send(JSON.stringify({
        type: 'join-meeting',
        meetingId,
        participantId,
        participantName,
      }));
    };

    ws.current.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);

        switch (data.type) {
          case 'participant-joined':
            // Invalidate and refetch the participants
            queryClient.invalidateQueries({
              queryKey: ['/api/meetings', meetingId, 'participants']
            });
            // Also refetch to ensure immediate update
            queryClient.refetchQueries({
              queryKey: ['/api/meetings', meetingId, 'participants']
            });
            break;

          case 'participant-left':
            setParticipants(prev => prev.filter(p => p.id !== data.participantId));
            break;

          case 'participant-updated':
            setParticipants(prev => prev.map(p => 
              p.id === data.participantId ? { ...p, ...data.updates } : p
            ));
            break;

          case 'new-chat-message':
            const participant = participants.find(p => p.id === data.message.participantId);
            setChatMessages(prev => [...prev, {
              ...data.message,
              participantName: participant?.name || participantName,
            }]);
            break;

          case 'webrtc-signal':
            // Handle WebRTC signaling
            break;
        }
      } catch (error) {
        console.error('Error parsing WebSocket message:', error);
      }
    };

    ws.current.onclose = () => {
      setIsConnected(false);
    };

    ws.current.onerror = (error) => {
      console.error('WebSocket error:', error);
      setIsConnected(false);
    };

    return () => {
      ws.current?.close();
    };
  }, [meetingId, participantId, participantName, queryClient]);

  const sendMessage = (message: string) => {
    if (ws.current?.readyState === WebSocket.OPEN) {
      ws.current.send(JSON.stringify({
        type: 'chat-message',
        text: message,
      }));
    }
  };

  return {
    participants,
    chatMessages,
    sendMessage,
    isConnected,
  };
}
"}

{"output":"import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, boolean, json } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const meetings = pgTable("meetings", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  hostId: varchar("host_id").notNull(),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").default(sql`now()`),
});

export const participants = pgTable("participants", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  meetingId: varchar("meeting_id").notNull().references(() => meetings.id),
  name: text("name").notNull(),
  socketId: text("socket_id"),
  isHost: boolean("is_host").default(false),
  isMuted: boolean("is_muted").default(false),
  isCameraOff: boolean("is_camera_off").default(false),
  isScreenSharing: boolean("is_screen_sharing").default(false),
  joinedAt: timestamp("joined_at").default(sql`now()`),
});

export const chatMessages = pgTable("chat_messages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  meetingId: varchar("meeting_id").notNull().references(() => meetings.id),
  participantId: varchar("participant_id").notNull().references(() => participants.id),
  message: text("message").notNull(),
  timestamp: timestamp("timestamp").default(sql`now()`),
});

export const insertMeetingSchema = createInsertSchema(meetings).pick({
  title: true,
  hostId: true,
});

export const insertParticipantSchema = createInsertSchema(participants).pick({
  meetingId: true,
  name: true,
  isHost: true,
});

export const insertChatMessageSchema = createInsertSchema(chatMessages).pick({
  meetingId: true,
  participantId: true,
  message: true,
});

export type InsertMeeting = z.infer<typeof insertMeetingSchema>;
export type Meeting = typeof meetings.$inferSelect;
export type InsertParticipant = z.infer<typeof insertParticipantSchema>;
export type Participant = typeof participants.$inferSelect;
export type InsertChatMessage = z.infer<typeof insertChatMessageSchema>;
export type ChatMessage = typeof chatMessages.$inferSelect;
"}
