export default function Meeting() {
  const { id } = useParams();
  const search = useSearch();
  const { toast } = useToast();
  const [participantId, setParticipantId] = useState<string | null>(null);

  const searchParams = new URLSearchParams(search);
  const isHost = searchParams.get('host') === 'true';
  const participantName = searchParams.get('name') || 'Anonymous';

  const { data: meeting, isLoading: meetingLoading, error: meetingError } = useQuery({
    queryKey: ['/api/meetings', id],
    enabled: !!id,
  });

  const addParticipantMutation = useMutation({
    mutationFn: async (data: { name: string; isHost: boolean }) => {
      const response = await apiRequest("POST", `/api/meetings/${id}/participants`, data);
      return response.json();
    },
    onSuccess: (participant) => {
      setParticipantId(participant.id);
      // Invalidate and refetch participants list to ensure immediate update
      queryClient.invalidateQueries({
        queryKey: ['/api/meetings', id, 'participants']
      });
      queryClient.refetchQueries({
        queryKey: ['/api/meetings', id, 'participants']
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to join meeting",
        variant: "destructive",
      });
    },
  });

  useEffect(() => {
    if (meeting && !participantId) {
      addParticipantMutation.mutate({
        name: participantName,
        isHost,
      });
    }
  }, [meeting, participantId, participantName, isHost]);

  if (meetingLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="flex items-center space-x-2">
          <Loader2 className="w-6 h-6 animate-spin" />
          <span className="text-muted-foreground">Loading meeting...</span>
        </div>
      </div>
    );
  }

  if (meetingError || !meeting) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-foreground mb-2">Meeting Not Found</h1>
          <p className="text-muted-foreground">The meeting you're looking for doesn't exist or has ended.</p>
        </div>
      </div>
    );
  }

  if (!participantId) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="flex items-center space-x-2">
          <Loader2 className="w-6 h-6 animate-spin" />
          <span className="text-muted-foreground">Joining meeting...</span>
        </div>
      </div>
    );
  }

  // At this point, TypeScript knows meeting is not null/undefined
  return (
    <VideoConferenceLayout 
      meeting={meeting as any}
      participantId={participantId}
      participantName={participantName}
      isHost={isHost}
    />
  );
}
