{"file_path":"/Users/ORDEROFCODE/COM-PSU-Rizal/scripts/create-test-users.ts","content":"import { supabase } from '../lib/supabaseClient';
import { createClient } from '@supabase/supabase-js';

// Get these from your Supabase project settings
const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL || '';
const supabaseServiceKey = process.env.SUPABASE_SERVICE_KEY || '';

if (!supabaseUrl || !supabaseServiceKey) {
  console.error('Missing Supabase URL or Service Key. Please set NEXT_PUBLIC_SUPABASE_URL and SUPABASE_SERVICE_KEY in your environment variables.');
  process.exit(1);
}

// Create a service client with full access
const serviceClient = createClient(supabaseUrl, supabaseServiceKey);

interface TestUser {
  email: string;
  password: string;
  firstName: string;
  lastName: string;
  role: 'student' | 'faculty' | 'admin';
}

const testUsers: TestUser[] = [
  {
    email: 'admin@com-psu-rizal.com',
    password: 'admin',
    firstName: 'Admin',
    lastName: 'User',
    role: 'admin'
  },
  {
    email: 'user1@com-psu-rizal.com',
    password: 'user1',
    firstName: 'User',
    lastName: 'One',
    role: 'student'
  },
  {
    email: 'user2@com-psu-rizal.com',
    password: 'user2',
    firstName: 'User',
    lastName: 'Two',
    role: 'student'
  }
];

async function createTestUsers() {
  console.log('Creating test users...');

  for (const user of testUsers) {
    try {
      // Sign up the user with Supabase Auth
      const { data: authData, error: authError } = await serviceClient.auth.signUp({
        email: user.email,
        password: user.password,
        options: {
          data: {
            first_name: user.firstName,
            last_name: user.lastName,
            role: user.role
          }
        }
      });

      if (authError) {
        console.error(`Error creating user ${user.email}:`, authError.message);
        continue;
      }

      // If signup is successful, create a user record in the database
      if (authData.user) {
        const { error: insertError } = await serviceClient
          .from('users')
          .insert([
            {
              id: authData.user.id,
              email: user.email,
              first_name: user.firstName,
              last_name: user.lastName,
              role: user.role,
              created_at: new Date().toISOString()
            }
          ]);

        if (insertError) {
          console.error(`Error creating user record for ${user.email}:`, insertError.message);
          // If we can't create the user record, sign out the user
          await serviceClient.auth.signOut();
          continue;
        }

        console.log(`Successfully created user: ${user.email} (${user.role})`);
      }
    } catch (error) {
      console.error(`Error creating user ${user.email}:`, error);
    }
  }

  console.log('Test user creation completed.');
}

createTestUsers().catch(console.error);"}

{"content":"import { supabase } from '../lib/supabaseClient';

interface Meeting {
  id: string;
  title: string;
  hostId: string;
  isActive: boolean;
  createdAt: string;
}

interface Participant {
  id: string;
  meetingId: string;
  name: string;
  socketId: string;
  isHost: boolean;
  isMuted: boolean;
  isCameraOff: boolean;
  isScreenSharing: boolean;
  joinedAt: string;
}

interface ChatMessage {
  id: string;
  meetingId: string;
  participantId: string;
  message: string;
  timestamp: string;
}

export class MeetingService {
  static async createMeeting(title: string, hostId: string): Promise<Meeting | null> {
    try {
      const { data, error } = await supabase
        .from('meetings')
        .insert([
          {
            title,
            host_id: hostId,
            is_active: true
          }
        ])
        .select()
        .single();

      if (error) {
        console.error('Error creating meeting:', error);
        return null;
      }

      return data as Meeting;
    } catch (error) {
      console.error('Error creating meeting:', error);
      return null;
    }
  }

  static async getMeeting(meetingId: string): Promise<Meeting | null> {
    try {
      const { data, error } = await supabase
        .from('meetings')
        .select('*')
        .eq('id', meetingId)
        .single();

      if (error) {
        console.error('Error getting meeting:', error);
        return null;
      }

      return data as Meeting;
    } catch (error) {
      console.error('Error getting meeting:', error);
      return null;
    }
  }

  static async addParticipant(
    meetingId: string,
    name: string,
    isHost: boolean = false
  ): Promise<Participant | null> {
    try {
      const { data, error } = await supabase
        .from('participants')
        .insert([
          {
            meeting_id: meetingId,
            name,
            is_host: isHost,
            is_muted: false,
            is_camera_off: false,
            is_screen_sharing: false
          }
        ])
        .select()
        .single();

      if (error) {
        console.error('Error adding participant:', error);
        return null;
      }

      return data as Participant;
    } catch (error) {
      console.error('Error adding participant:', error);
      return null;
    }
  }

  static async getParticipants(meetingId: string): Promise<Participant[]> {
    try {
      const { data, error } = await supabase
        .from('participants')
        .select('*')
        .eq('meeting_id', meetingId);

      if (error) {
        console.error('Error getting participants:', error);
        return [];
      }

      return data as Participant[];
    } catch (error) {
      console.error('Error getting participants:', error);
      return [];
    }
  }

  static async addChatMessage(
    meetingId: string,
    participantId: string,
    message: string
  ): Promise<ChatMessage | null> {
    try {
      const { data, error } = await supabase
        .from('chat_messages')
        .insert([
          {
            meeting_id: meetingId,
            participant_id: participantId,
            message
          }
        ])
        .select()
        .single();

      if (error) {
        console.error('Error adding chat message:', error);
        return null;
      }

      return data as ChatMessage;
    } catch (error) {
      console.error('Error adding chat message:', error);
      return null;
    }
  }

  static async getChatMessages(meetingId: string): Promise<ChatMessage[]> {
    try {
      const { data, error } = await supabase
        .from('chat_messages')
        .select(`
          *,
          participants(name)
        `)
        .eq('meeting_id', meetingId)
        .order('timestamp', { ascending: true });

      if (error) {
        console.error('Error getting chat messages:', error);
        return [];
      }

      // Map the data to include participant names
      return data.map((msg: any) => ({
        ...msg,
        participantName: msg.participants?.name || 'Unknown'
      })) as ChatMessage[];
    } catch (error) {
      console.error('Error getting chat messages:', error);
      return [];
    }
  }
