{"output":"import { type NextRequest } from 'next/server'
import { updateSession } from '@/utils/supabase/middleware'

export async function middleware(request: NextRequest) {
  return await updateSession(request)
}

export const config = {
  matcher: [
    /*
     * Match all request paths except for the ones starting with:
     * - api (API routes)
     * - _next/static (static files)
     * - _next/image (image optimization files)
     * - favicon.ico (favicon file)
     */
    '/((?!api|_next/static|_next/image|favicon.ico).*)',
  ],
}"}

{"output":"import { createServerClient } from '@supabase/ssr'
import { NextResponse, type NextRequest } from 'next/server'

export async function updateSession(request: NextRequest) {
  let supabaseResponse = NextResponse.next({
    request,
  })

  const supabase = createServerClient(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
    {
      cookies: {
        getAll() {
          return request.cookies.getAll()
        },
        setAll(cookiesToSet) {
          cookiesToSet.forEach(({ name, value, options }) =>
            request.cookies.set(name, value)
          )
          supabaseResponse = NextResponse.next({
            request,
          })
          cookiesToSet.forEach(({ name, value, options }) =>
            supabaseResponse.cookies.set(name, value, options)
          )
        },
      },
    }
  )

  // Refresh session if expired - required for Server Components
  await supabase.auth.getSession()

  return supabaseResponse
}"}

{"output":"import { SiteHeader } from "@/components/site-header"
import { Hero } from "@/components/hero"
import { Features } from "@/components/features"
import { LogoMarquee } from "@/components/logo-marquee"
import { Plans } from "@/components/plans"
import { AppverseFooter } from "@/components/appverse-footer"
import Script from "next/script"
import { createClient } from '@/utils/supabase/server'
import { cookies } from 'next/headers'
import { redirect } from 'next/navigation'

// ✅ Force static generation for low TTFB
export const dynamic = "force-static"

export default async function Page() {
  // Check if user is already logged in
  const cookieStore = await cookies()
  const supabase = createClient(cookieStore)
  const { data: { session } } = await supabase.auth.getSession()

  // If user is logged in, redirect to dashboard
  if (session) {
    // For now, we'll redirect to the auth page which will handle role-based redirection
    redirect('/dashboard')
  }

  // Structured data for pricing
  const pricingStructuredData = {
    "@context": "https://schema.org",
    "@type": "WebPageElement",
    "@id": "https://com-psu-rizal.com/#pricing",
    name: "Pricing Plans",
    description: "Video conferencing pricing plans - Basic, Pro, and Enterprise packages for all business needs",
    url: "https://com-psu-rizal.com/#pricing",
    mainEntity: {
      "@type": "PriceSpecification",
      name: "Video Conferencing Services",
      description: "Professional video conferencing services with three pricing tiers",
      offers: [
        {
          "@type": "Offer",
          name: "Basic Plan",
          price: "0",
          priceCurrency: "USD",
          description: "Free plan for individuals and small teams",
        },
        {
          "@type": "Offer",
          name: "Pro Plan",
          price: "9.99",
          priceCurrency: "USD",
          description: "Advanced features for growing teams",
        },
        {
          "@type": "Offer",
          name: "Enterprise Plan",
          price: "29.99",
          priceCurrency: "USD",
          description: "Unlimited meetings for large organizations",
        },
      ],
    },
  }

  // Structured data for main page
  const pageStructuredData = {
    "@context": "https://schema.org",
    "@type": "WebPage",
    "@id": "https://com-psu-rizal.com/",
    name: "Collaboration Online Meet | Real-time Video Conferencing",
    description:
      "Experience seamless video conferencing with Collaboration Online Meet. High-quality video calls, screen sharing, and real-time chat for all your collaboration needs.",
    url: "https://com-psu-rizal.com/",
    mainEntity: {
      "@type": "Organization",
      name: "Collaboration Online Meet",
      url: "https://com-psu-rizal.com",
      sameAs: [
        "https://twitter.com/compsurizal",
        "https://www.youtube.com/@compsurizal",
        "https://instagram.com/compsurizal",
        "https://threads.com/compsurizal",
      ],
    },
    hasPart: [
      {
        "@type": "WebPageElement",
        "@id": "https://com-psu-rizal.com/#pricing",
        name: "Pricing Section",
        url: "https://com-psu-rizal.com/#pricing",
      },
    ],
  }

  return (
    <>
      <main className="min-h-[100dvh] text-white">
        <SiteHeader />
        <Hero />
        <Features />
        <LogoMarquee />
        <Plans />
        <AppverseFooter />
      </main>

      {/* JSON-LD structured data */}
      <Script
        id="pricing-structured-data"
        type="application/ld+json"
        strategy="afterInteractive"
        dangerouslySetInnerHTML={{
          __html: JSON.stringify(pricingStructuredData),
        }}
      />

      <Script
        id="page-structured-data"
        type="application/ld+json"
        strategy="afterInteractive"
        dangerouslySetInnerHTML={{
          __html: JSON.stringify(pageStructuredData),
        }}
      />
    </>
  )
}"}

{"output":"import { createServerClient, type CookieOptions } from "@supabase/ssr";
import { cookies } from "next/headers";

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
const supabaseKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY;

export const createClient = (cookieStore: Awaited<ReturnType<typeof cookies>>) => {
  return createServerClient(
    supabaseUrl!,
    supabaseKey!,
    {
      cookies: {
        getAll() {
          return cookieStore.getAll();
        },
        setAll(cookiesToSet) {
          try {
            cookiesToSet.forEach(({ name, value, options }) => cookieStore.set(name, value, options));
          } catch {
            // The `setAll` method was called from a Server Component.
            // This can be ignored if you have middleware refreshing
            // user sessions.
          }
        },
      },
    },
  );
};"}

